class Randomizer{constructor(t=Date.now()){this.seed=t,this.cursor=0}float(t=0,e=1){return t+(Math.sin(this.seed+this.cursor++)+1)/2%1*(e-t)}int(t=1,e=100){return Math.round(this.float(t,e))}boolean(){return this.float()>=.5}string(t=16,e=!0){let r=this.float().toString(t).slice(2);return e&&(r=r.toUpperCase()),r}reset(t=this.seed){return this.seed=t,this.cursor=0,this}}const Random=new Randomizer;Object.defineProperty(Random,"Randomizer",{value:Randomizer});export default Random;